// === VALIDACI√ìN Y NORMALIZACI√ìN DE DATOS ===
const data = items[0].json;

// üßπ Normaliza y limpia los nombres de campos del formulario
const nuevo = {
  Nombre: data["Nombre"] || data["Nombre:"] || data["nombre"] || "",
  Apellido: data["Apellido"] || data["Apellido:"] || data["apellido"] || "",
  Email: data["Email"] || data["Email:"] || data["email"] || "",
  Frecuencia: data["Frecuencia"] || data["Elije la frecuencia con la cual deseas recibir ofertas:"] || "",
  Modalidad: data["Modalidad"] || data["Modalidad:"] || data["modalidad"] || "",
  Region: data["Regi√≥n"] || data["Region"] || data["Elije la Regi√≥n en la cual te gustar√≠a centrar tu busqueda:"] || ""
};

// ‚ö†Ô∏è Validar campos vac√≠os
const camposFaltantes = Object.entries(nuevo)
  .filter(([_, valor]) => !valor || valor.trim() === "")
  .map(([clave]) => clave);

// üìß Validar formato de correo electr√≥nico
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
const emailValido = emailRegex.test(nuevo.Email);

// üö® Si falta un campo o el correo es inv√°lido ‚Üí detener flujo
if (camposFaltantes.length > 0 || !emailValido) {
  return [
    {
      json: {
        estado: "ERROR",
        mensaje: "Faltan datos o el correo no es v√°lido.",
        faltantes: camposFaltantes,
        email: nuevo.Email
      }
    }
  ];
}

// ‚úÖ Si todo est√° bien, devolver datos normalizados
return [
  {
    json: {
      ...nuevo,
      estado: "ACTIVA"
    }
  }
];

// --- Helpers ---
function norm(s){
  return String(s || '')
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '') // sin acentos
    .toLowerCase().trim();
}

function formatLocal(dt){
  const pad = n => String(n).padStart(2,'0');
  return `${dt.getFullYear()}-${pad(dt.getMonth()+1)}-${pad(dt.getDate())} ${pad(dt.getHours())}:${pad(dt.getMinutes())}:${pad(dt.getSeconds())}`;
}

function nextFrom(freq, base = new Date()){
  const f = norm(freq);
  const d = new Date(base);
  if (/30\s*min/.test(f))              d.setMinutes(d.getMinutes() + 30);
  else if (/^4\s*dias?$/.test(f))      d.setDate(d.getDate() + 4);
  else if (/^15\s*dias?$/.test(f))     d.setDate(d.getDate() + 15);
  else if (/semana/.test(f))           d.setDate(d.getDate() + 7);
  else if (/mes/.test(f))              d.setMonth(d.getMonth() + 1);
  else if (/24\s*h/.test(f) || /dia/.test(f))
                                       d.setDate(d.getDate() + 1);
  else                                 d.setDate(d.getDate() + 7); // default semanal
  return formatLocal(d);
}

const data = items[0].json;
const freq = data.Frecuencia || data['Elije la frecuencia con la cual deseas recibir ofertas:'] || '';
data.next_run = nextFrom(freq);
data.estado   = 'ACTIVA';
return [{ json: data }];

// --- helpers ---
function toDate(x) {
  if (x == null || x === '') return null;
  if (typeof x === 'number') {
    // Serial de Google Sheets (base 1899-12-30, en UTC)
    const base = new Date(Date.UTC(1899, 11, 30));
    return new Date(base.getTime() + x * 86400000);
  }
  const t = Date.parse(String(x));
  return Number.isNaN(t) ? null : new Date(t);
}

const now = new Date(); // Zona local del servidor n8n
const passed = [];
let skippedCount = 0;

for (const it of items) {
  const r = it.json || {};
  const raw = r.next_run ?? r.Next_run ?? r['next_run'];
  const d = toDate(raw);
  if (d && d <= now) {
    passed.push(it);
  } else {
    skippedCount++;
  }
}

// Si no hay items que procesar, devolver un NOOP para no cortar el flujo
if (passed.length === 0) {
  return [{
    json: {
      _noop: true,
      status: 'NO_MATCH',
      skipped: skippedCount,
      note: 'No hay registros con next_run vencido'
    }
  }];
}

return passed;

// Si llega un marcador NOOP, simplemente lo pasamos tal cual o lo ignoramos
const hasNoopOnly = items.length === 1 && items[0]?.json?._noop === true;
if (hasNoopOnly) {
  // Si prefieres no propagar nada, devuelve el NOOP:
  return items;
  // O, si prefieres cortar aqu√≠ sin error:
  // return [];
}

// --- Helpers robustos ---
function limpiarRegion(r) {
  if (r == null) return '';
  return String(r).split(/[‚Äì-]/)[0].trim();
}

function pick(obj, keys, def='') {
  for (const k of keys) {
    const v = obj?.[k];
    if (v !== undefined && v !== null && String(v).trim() !== '') return v;
  }
  return def;
}

function toIsoDateMaybe(x) {
  if (x == null || x === '') return '';
  try {
    if (typeof x === 'number') {
      const base = new Date(Date.UTC(1899, 11, 30));
      return new Date(base.getTime() + x * 86400000).toISOString();
    }
    const d = new Date(String(x).replace(' ', 'T') + 'Z');
    return isNaN(d.getTime()) ? String(x) : d.toISOString();
  } catch {
    return String(x);
  }
}

const validItems = items.filter(it => !it.json._noop);

return validItems.map(i => {
  const r = i.json || {};
  const email      = String(pick(r, ['Email','email'])).trim();
  const modalidad  = String(pick(r, ['Modalidad','modalidad'])).trim().toLowerCase();
  const regionRaw  = pick(r, ['Regi√≥n','Region','REGION','region']);
  const frecuencia = String(pick(r, ['Frecuencia','frecuencia'])).trim();
  const next_run   = toIsoDateMaybe(pick(r, ['next_run','Next_run','NEXT_RUN']));

  return {
    json: {
      email,
      modalidad,
      region: limpiarRegion(regionRaw),
      frecuencia,
      next_run,
      status: 'READY'
    }
  };
});

// === PARSER INDEED v16 (desescapa \ y busca correctamente empresa y regi√≥n) ===
let html = $json.data ?? $json.body ?? "";
const jobs = [];

// 1Ô∏è‚É£ Limpieza agresiva
html = html
  .replace(/\\n/g, " ")   // eliminar saltos de l√≠nea escapados
  .replace(/\\t/g, " ")
  .replace(/\\r/g, " ")
  .replace(/\\"/g, '"')   // arreglar comillas escapadas
  .replace(/\\\\/g, "\\") // limpiar dobles barras
  .replace(/\s+/g, " ");

// 2Ô∏è‚É£ Dividir por cada oferta detectando <a ... data-jk o <div ... data-jk
const bloques = html.split(/<[^>]+data-jk="/).slice(1);

for (const b of bloques) {
  const bloque = '<div data-jk="' + b; // reconstruir bloque
  const titulo =
    (bloque.match(/title="([^"]+)"/i) ||
      bloque.match(/<span[^>]*id="jobTitle[^"]*"[^>]*>(.*?)<\/span>/i) ||
      [])[1]
      ?.replace(/<[^>]+>/g, "")
      ?.trim() || "";

  let link = (bloque.match(/href="([^"]+)"/i) || [])[1] || "";
  if (link && !link.startsWith("http")) link = `https://cl.indeed.com${link}`;

  const empresa =
    (bloque.match(/data-testid="company-name"[^>]*>([^<]+)</i) || [])[1]?.trim() ||
    (bloque.match(/cmp=([^"&]+)/i) || [])[1]?.replace(/-/g, " ") ||
    "Sin empresa";

  const region =
    (bloque.match(/data-testid="text-location"[^>]*>([^<]+)</i) || [])[1]?.trim() ||
    "Sin regi√≥n";

  const modalidad =
    /remot/i.test(region)
      ? "Remoto"
      : /h√≠brid/i.test(region)
      ? "H√≠brido"
      : "Presencial";

  if (titulo) {
    jobs.push({
      fuente: "Indeed",
      titulo,
      empresa,
      region,
      modalidad,
      link,
    });
  }
}

// 3Ô∏è‚É£ Salida
if (jobs.length === 0) {
  return [{ json: { mensaje: "No se extrajeron ofertas v√°lidas", total: 0 } }];
}

return jobs.map((x) => ({ json: x }));

// Soporta JSON (hits.hits) o respuesta String (intenta parsear; fallback HTML)
let text = $json.data ?? $json.body ?? null;
let hits = [];

if (typeof text === "string") {
  let t = text.trim();
  if (t.startsWith(")]}'")) t = t.slice(4).trim(); // anti-XSSI
  try { const js = JSON.parse(t); hits = js?.hits?.hits || []; } catch {}
} else if ($json?.hits?.hits) {
  hits = $json.hits.hits;
}

if (!hits.length) {
  // Fallback: intentar raspar enlaces si fue HTML
  const html = typeof text === "string" ? text : "";
  const re = /<a[^>]+href="([^"]+)"[^>]*>(.*?)<\/a>/gi;
  const alt = [];
  let m; 
  while ((m = re.exec(html)) && alt.length < 25) {
    const link = m[1];
    const titulo = (m[2] || "").replace(/<[^>]+>/g, "").trim();
    if (titulo && link) alt.push({ fuente:"Santander", titulo, link, region:"Sin regi√≥n", modalidad:"Presencial" });
  }
  return alt.length ? alt.map(x => ({ json: x })) 
                    : [{ json: { fuente: "Santander", mensaje: "Sin resultados o respuesta no JSON" } }];
}

const out = hits.map(h => {
  const s = h._source || h;
  const titulo = s?.title || "Sin t√≠tulo";
  const link = s?.applyUrl || s?.url || "";
  const region = (s?.location && (s.location.display_name || s.location.city)) || s?.city || "Sin regi√≥n";
  const modalidad = /remot/i.test(((s?.modality || "") + " " + region)) ? "Remoto" : "Presencial";
  return { fuente: "Santander", titulo, link, region, modalidad };
});

return out.map(x => ({ json: x }));

// === PARSER LABORUM v8 - Limpieza avanzada ===
const raw = $json.data || $json.body || "";
const text = String(raw).replace(/\r/g, "").trim();

// --- Expresi√≥n m√°s flexible ---
const regex = /\[(?:Nuevo|Actualizado|\#\#\#)?[^\]]*?(Pr√°ctica[^\#\n]+|Alumno en Pr√°ctica[^\#\n]*)[^\]]*?\#\#\#\s*([A-Z√Å√â√ç√ì√ö√ë√ú][^\#\n]+?)\s*(?:\#\#\#\s*([^#\n]+?))?\s*\#\#\#\s*(Presencial|Remoto|H√≠brido)[^\]]*\]\((https:\/\/www\.laborum\.cl\/empleos\/[^\)]+)\)/gi;

const out = [];
let match;

while ((match = regex.exec(text))) {
  let titulo   = (match[1] || "").replace(/[-‚Äì‚Äî]+/g, "").trim();
  let empresa  = (match[2] || "").trim();
  let region   = (match[3] || "").trim();
  let modalidad= (match[4] || "").trim() || "Presencial";
  let link     = (match[5] || "").trim();

  // --- LIMPIEZA ---
  // 1. Eliminar Markdown residual e im√°genes
  empresa = empresa.replace(/!\[.*?\]\(.*?\)/g, "").replace(/\s{2,}/g, " ").trim();
  region  = region.replace(/!\[.*?\]\(.*?\)/g, "").replace(/\s{2,}/g, " ").trim();
  titulo  = titulo.replace(/!\[.*?\]\(.*?\)/g, "").replace(/\s{2,}/g, " ").trim();

  // 2. Si empresa contiene "Regi√≥n", intercambiar
  if (/Regi√≥n/i.test(empresa)) {
    region = empresa;
    empresa = "Sin empresa";
  }

  // 3. Si regi√≥n est√° vac√≠a o sin "Regi√≥n", intentar buscar dentro del bloque
  if (!/Regi√≥n/i.test(region)) {
    const m = empresa.match(/([A-Z√Å√â√ç√ì√ö√ë√úa-z√±√º\s]+,\s*Regi√≥n\s*[A-Z√Å√â√ç√ì√ö√ë√úIVX]+)/);
    if (m) {
      region = m[1].trim();
      empresa = empresa.replace(m[0], "").trim();
    }
  }

  // 4. Recortar empresa si es descripci√≥n larga
  if (empresa.split(/\s+/).length > 6) {
    empresa = empresa
      .replace(/Somos.*|Compa√±√≠a.*|Empresa.*|holding.*|S\.A\..*|Sociedad.*|Limitada.*|con.*|del.*|la.*|los.*|las.*|de.*$/gi, "")
      .trim();
  }

  // 5. Limpieza final
  empresa = empresa.replace(/[,.-]+$/g, "").trim();
  region  = region.replace(/^Regi√≥n\s*/i, "").trim() || "Sin regi√≥n";
  titulo  = titulo.replace(/-{2,}|={2,}/g, "").trim();

  if (link) {
    out.push({
      fuente: "Laborum",
      titulo,
      empresa: empresa || "Sin empresa",
      region,
      modalidad,
      link
    });
  }
}

// --- Fallback m√≠nimo ---
if (out.length === 0) {
  const altRegex = /\[\s*(Pr√°ctica[^\#\n]+|Alumno en Pr√°ctica[^\#\n]*)[^\]]*\]\((https:\/\/www\.laborum\.cl\/empleos\/[^\)]+)\)/gi;
  let m;
  while ((m = altRegex.exec(text))) {
    out.push({
      fuente: "Laborum",
      titulo: (m[1] || "").trim(),
      empresa: "Sin empresa",
      region: "Sin regi√≥n",
      modalidad: "Presencial",
      link: m[2]
    });
  }
}

// --- Eliminar duplicados ---
const unicos = out.filter((v, i, a) => a.findIndex(t => t.link === v.link) === i);

return unicos.length
  ? unicos.map(x => ({ json: x }))
  : [{ json: { fuente: "Laborum", mensaje: "Sin resultados v√°lidos", total: 0 } }];

// === APLANA objetos anidados (arregla Merge Results) ===
items = items.map(i => {
  if (i.json?.json && typeof i.json.json === 'object') {
    return { json: { ...i.json.json } };
  }
  return i;
});

// === Filtro por preferencias de regi√≥n y modalidad ===
// Soporta modalidad seleccionada como string ("remoto") o lista ["remoto","presencial"]

const prefs = $items('Split In Batches', 0, 0)?.json || {};
const prefRegionRaw = prefs.region || '';
const prefModaRaw   = prefs.modalidad || '';

// Helpers de normalizaci√≥n
const sinAcentos = s => (s || '').normalize('NFD').replace(/[\u0300-\u036f]/g, '');
const normTxt = s => sinAcentos(String(s||'').toLowerCase())
  .replace(/\s+/g,' ')
  .trim();

// Canonicaliza modalidad a: "Remoto" | "H√≠brido" | "Presencial" | ""
function canonModa(s) {
  const t = normTxt(s);
  if (!t) return '';
  if (/(remot|teletrab|home ?office|desde casa|anywhere|wfh)/.test(t)) return 'Remoto';
  if (/(hibrid|h√≠brid|mixt|semi|flex)/.test(t))                     return 'H√≠brido';
  if (/(presenc|on ?site|en sitio|oficin|sede)/.test(t))           return 'Presencial';
  return '';
}

// Lee preferencia de modalidad (string o array/coma-separado) -> Set can√≥nico
function readModaPref(x) {
  if (Array.isArray(x)) return new Set(x.map(canonModa).filter(Boolean));
  const t = String(x || '').split(',').map(v => v.trim()).filter(Boolean);
  return new Set(t.map(canonModa).filter(Boolean));
}

const prefModaSet = readModaPref(prefModaRaw);
const prefRegion  = normTxt(prefRegionRaw);

// Reglas de compatibilidad: si prefieres "Remoto", aceptamos tambi√©n "H√≠brido"
function matchModa(prefSet, itemModa) {
  if (!prefSet.size) return true;              // sin preferencia -> todos
  const m = canonModa(itemModa);
  if (!m) return false;
  if (prefSet.has('H√≠brido') && m === 'H√≠brido') return true;
  if (prefSet.has('Presencial') && m === 'Presencial') return true;
  if (prefSet.has('Remoto')) return (m === 'Remoto' || m === 'H√≠brido');
  return false;
}

// Normaliza y filtra
const out = [];
for (const { json } of items) {
  if (!json.link) continue;

  // Completa/estandariza modalidad del item usando varias pistas
  json.modalidad = canonModa(json.modalidad) 
                || canonModa(json.region) 
                || canonModa(json.titulo) 
                || 'Presencial'; // por defecto asumimos presencial

  // Regi√≥n: tolerante (RM/Metropolitana/Santiago; O'Higgins/OHiggins, etc.)
  const rItem = normTxt(json.region);
  const passRegion = !prefRegion
    || rItem.includes(prefRegion)
    || (prefRegion === 'rm' && (rItem.includes('metropolitana') || rItem.includes('santiago')))
    || (prefRegion.includes('ohiggins') && rItem.includes('ohiggins'));

  const passModa = matchModa(prefModaSet, json.modalidad);

  if (passRegion && passModa) out.push({ json });
}

return out;

// Limpia par√°metros de tracking en los links antes de deduplicar
const stripParams = [
  'utm_source','utm_medium','utm_campaign','utm_term','utm_content',
  'gclid','fbclid','fccid','vjs','from','ad','dq'
];

return items.map(({ json }) => {
  if (json.link && typeof json.link === 'string') {
    // normaliza entidades HTML tipo &amp;
    let href = json.link.trim().replace(/&amp;/g, '&');

    try {
      // Soporta links relativos (Indeed a veces trae /rc/clk?...):
      if (href.startsWith('/')) href = 'https://cl.indeed.com' + href;

      const u = new URL(href);
      // elimina par√°metros de tracking
      stripParams.forEach(p => u.searchParams.delete(p));
      // quita el fragmento #...
      u.hash = '';
      // normaliza protocolo a https si ven√≠a http
      if (u.protocol === 'http:') u.protocol = 'https:';

      json.link = u.toString();
    } catch {
      // si falla el parseo, al menos quita &amp; y espacios
      json.link = href;
    }
  }
  return { json };
});

// === APLANADOR UNIVERSAL + EMAIL BUILDER PRO ===

// --- Funciones base ---
function unbox(obj) {
  let j = obj?.json ?? obj ?? {};
  if (j && typeof j === 'object' && j.json && typeof j.json === 'object') j = j.json;
  for (const k of ['oferta','payload','data','result','resultado','item'])
    if (j && typeof j[k] === 'object' && j[k] !== null) { j = j[k]; break; }
  return j || {};
}
function esc(s){ return String(s ?? '').replace(/[&<>"']/g, m => ({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;',"'":'&#39;'}[m])); }
function pick(...xs){ for (const x of xs){ if (x !== undefined && x !== null && String(x).trim() !== '') return x; } return ''; }

// --- 1) Aplanar y colectar shapes para debug ---
const shapes = {};
const filasRaw = items.map(unbox);
for (const r of filasRaw) {
  const keys = Object.keys(r).sort().join('|');
  shapes[keys] = (shapes[keys] || 0) + 1;
}

// --- 2) Normalizar ---
const filas = filasRaw
  .map(r => ({
    fuente:   pick(r.fuente, r.source, r.origen, 'desconocida'),
    titulo:   pick(r.titulo, r.title, r.nombre),
    empresa:  pick(r.empresa, r.company, r.organizacion, 'Sin empresa'),
    region:   pick(r.region, r.ubicacion, r.location),
    modalidad:pick(r.modalidad, r.mode, r.tipoModalidad),
    link:     pick(r.link, r.url, r.href)
  }))
  .filter(r => r.titulo || r.link);

// --- 3) Extraer preferencias (asumimos del primer registro) ---
const prefRegion = filas[0]?.region && filas[0].region !== 'Sin regi√≥n' ? filas[0].region : 'No especificada';
const prefModalidad = filas[0]?.modalidad && filas[0].modalidad !== 'Presencial' ? filas[0].modalidad : 'Presencial';

// --- 4) M√©trica por fuente ---
const porFuente = {};
for (const r of filas) porFuente[r.fuente] = (porFuente[r.fuente] || 0) + 1;
const stats = Object.entries(porFuente)
  .map(([k,v]) => `<li><b>${esc(k)}</b>: ${v}</li>`)
  .join('');

// --- 5) Tabla de resultados ---
const rowsHtml = filas.map((r, i) => `
  <tr>
    <td style="text-align:center;color:#555;">${i+1}</td>
    <td style="font-weight:500;">${esc(r.titulo)}</td>
    <td style="color:#444;">${esc(r.empresa)}</td>
    <td style="color:#777;">${esc(r.fuente)}</td>
    <td style="text-align:center;">
      ${r.link ? `<a href="${esc(r.link)}" target="_blank" style="color:#2563eb;text-decoration:none;">Ver oferta</a>` : ''}
    </td>
  </tr>
`).join('');

// --- 6) Email destinatario ---
function pickEmail(obj){ if(!obj) return ''; const v = obj.Email ?? obj.email ?? obj.to ?? ''; return String(v).trim(); }
let emailTo = '';
try {
  const v = $items('Validar datos', 0, 0);
  if (Array.isArray(v) && v[0]?.json) emailTo = pickEmail(v[0].json);
} catch {}
if (!emailTo) {
  try {
    const s = $items('Split In Batches', 0, 0);
    if (Array.isArray(s) && s[0]?.json) emailTo = pickEmail(s[0].json);
  } catch {}
}
if (!emailTo && items[0]?.json) emailTo = pickEmail(items[0].json);
emailTo = String(emailTo || '').trim();

// --- 7) HTML final ---
const total = filas.length;
const shapesDebug = Object.entries(shapes)
  .map(([k,v]) => `<li><code>${esc(k)}</code>: ${v}</li>`).join('');

const html = `
  <div style="font-family:'Segoe UI',Arial,sans-serif;max-width:900px;margin:auto;background:#f9fafb;border-radius:8px;overflow:hidden;">
    <div style="background:#2563eb;color:white;padding:16px 20px;">
      <h2 style="margin:0;font-weight:600;">Resultados de b√∫squeda de pr√°cticas</h2>
      <p style="margin:4px 0 0;font-size:14px;">Total encontrados: <b>${total}</b></p>
    </div>

    <div style="padding:20px 24px;">
      <div style="margin-bottom:10px;padding:12px 16px;background:#eef2ff;border-radius:6px;">
        <p style="margin:0;font-size:14px;color:#374151;">
          <b>Regi√≥n preferida:</b> ${esc(prefRegion)}<br>
          <b>Modalidad deseada:</b> ${esc(prefModalidad)}
        </p>
      </div>

      ${stats ? `<p style="margin-bottom:4px;">Distribuci√≥n por fuente:</p><ul style="margin-top:4px;">${stats}</ul>` : '<p><i>Sin conteo por fuente.</i></p>'}

      <table border="0" cellpadding="8" cellspacing="0" style="border-collapse:collapse;width:100%;margin-top:10px;background:white;border:1px solid #e5e7eb;">
        <thead style="background:#f3f4f6;">
          <tr>
            <th style="text-align:center;">#</th>
            <th style="text-align:left;">T√≠tulo</th>
