// helpers del punto 2
function norm(s){
  return String(s || '')
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
    .toLowerCase().trim();
}
function formatLocal(dt){
  const pad = n => String(n).padStart(2,'0');
  return `${dt.getFullYear()}-${pad(dt.getMonth()+1)}-${pad(dt.getDate())} ${pad(dt.getHours())}:${pad(dt.getMinutes())}:${pad(dt.getSeconds())}`;
}
function nextFrom(freq, base = new Date()){
  const f = norm(freq);
  const d = new Date(base);
  if (/30\s*min/.test(f))              d.setMinutes(d.getMinutes() + 30);
  else if (/^4\s*dias?$/.test(f))      d.setDate(d.getDate() + 4);
  else if (/^15\s*dias?$/.test(f))     d.setDate(d.getDate() + 15);
  else if (/semana/.test(f))           d.setDate(d.getDate() + 7);
  else if (/mes/.test(f))              d.setMonth(d.getMonth() + 1);
  else if (/24\s*h/.test(f) || /dia/.test(f))
                                       d.setDate(d.getDate() + 1);
  else                                 d.setDate(d.getDate() + 7);
  return formatLocal(d);
}

