// --- helpers robustos para convertir next_run ---
function toDate(x) {
  if (x == null || x === '') return null;

  // Log del valor original
  console.log("raw next_run:", x);

  if (typeof x === 'number') {
    const base = new Date(Date.UTC(1899, 11, 30));
    const d = new Date(base.getTime() + x * 86400000);
    console.log("→ parsed as number:", d);
    return d;
  }

  const s = String(x).trim();
  console.log("raw string:", s);

  let d = new Date(s.includes('T') ? s : s.replace(' ', 'T'));
  console.log("→ parsed direct:", d);

  if (!isNaN(d)) return d;

  d = new Date((s.includes('T') ? s : s.replace(' ', 'T')) + 'Z');
  console.log("→ parsed as UTC:", d);

  return isNaN(d) ? null : d;
}

const now = new Date();
console.log("NOW:", now);

const out = [];
let skipped = 0;

for (const it of items) {
  const r = it.json || {};
  const raw = r.next_run ?? r.Next_run ?? r['next_run'];

  console.log("ITEM:", r);
  console.log("next_run FIELD BEFORE PARSING:", raw);

  const d = toDate(raw);

  console.log("parsed date:", d);

  if (d && d <= now) {
    console.log("✔ next_run vencido → PASA");
    out.push(it);
  } else {
    console.log("❌ next_run futuro o inválido → SKIP");
    skipped++;
  }
}

console.log("TOTAL PASAN:", out.length);

if (out.length === 0) {
  return [{ 
    json: { 
      _noop: true, 
      status: 'NO_MATCH', 
      skipped,
      note: 'No hay registros con next_run vencido',
      debug_items: items.map(i => i.json)
    } 
  }];
}

return out;

// === Normalizar payload de un solo usuario ===
// Este nodo recibe 1 item por vez desde Split In Batches
// y devuelve un objeto limpio y estandarizado del usuario actual.

const r = $json || {};

if (r._skip) {
  return []; // No procesar este item
} // usuario actual (garantizado por Split In Batches)

// Helpers de extracción segura
function pick(obj, keys) {
  for (const k of keys) {
    if (obj[k] !== undefined && obj[k] !== null && String(obj[k]).trim() !== '') {
      return obj[k];
    }
  }
  return '';
}

function limpiarRegion(txt) {
  if (!txt) return '';
  return String(txt).split(/[–-]/)[0].trim();   // toma la parte antes del guion
}

function toIsoDateMaybe(x) {
  if (!x) return '';
  try {
    const d = new Date(String(x).replace(' ', 'T'));
    return isNaN(d.getTime()) ? '' : d.toISOString();
  } catch {
    return '';
  }
}

// --- Normalizar campos del usuario actual ---
const email      = String(pick(r, ['Email','email'])).trim();
const modalidad  = String(pick(r, ['Modalidad','modalidad'])).trim();
const regionRaw  = pick(r, ['Región','Region','region','REGION']);
const frecuencia = String(pick(r, ['Frecuencia','frecuencia'])).trim();
const next_run   = toIsoDateMaybe(pick(r, ['next_run','Next_run','NEXT_RUN']));

// Salida del nodo → SOLO datos del usuario actual (no mezcla nada)
return [
  {
    json: {
      email,
      modalidad,
      region: limpiarRegion(regionRaw),
      frecuencia,
      next_run,
      status: 'READY'
    }
  }
];

// === PARSER INDEED v16 (desescapa \ y busca correctamente empresa y región) ===
let html = $json.data ?? $json.body ?? "";
const jobs = [];

// 1️⃣ Limpieza agresiva
html = html
  .replace(/\\n/g, " ")   // eliminar saltos de línea escapados
  .replace(/\\t/g, " ")
  .replace(/\\r/g, " ")
  .replace(/\\"/g, '"')   // arreglar comillas escapadas
  .replace(/\\\\/g, "\\") // limpiar dobles barras
  .replace(/\s+/g, " ");

// 2️⃣ Dividir por cada oferta detectando <a ... data-jk o <div ... data-jk
const bloques = html.split(/<[^>]+data-jk="/).slice(1);

for (const b of bloques) {
  const bloque = '<div data-jk="' + b; // reconstruir bloque
  const titulo =
    (bloque.match(/title="([^"]+)"/i) ||
      bloque.match(/<span[^>]*id="jobTitle[^"]*"[^>]*>(.*?)<\/span>/i) ||
      [])[1]
      ?.replace(/<[^>]+>/g, "")
      ?.trim() || "";

  let link = (bloque.match(/href="([^"]+)"/i) || [])[1] || "";
  if (link && !link.startsWith("http")) link = `https://cl.indeed.com${link}`;

  const empresa =
    (bloque.match(/data-testid="company-name"[^>]*>([^<]+)</i) || [])[1]?.trim() ||
    (bloque.match(/cmp=([^"&]+)/i) || [])[1]?.replace(/-/g, " ") ||
    "Sin empresa";

  const region =
    (bloque.match(/data-testid="text-location"[^>]*>([^<]+)</i) || [])[1]?.trim() ||
    "Sin región";

  const modalidad =
    /remot/i.test(region)
      ? "Remoto"
      : /híbrid/i.test(region)
      ? "Híbrido"
      : "Presencial";

  if (titulo) {
    jobs.push({
      fuente: "Indeed",
      titulo,
      empresa,
      region,
      modalidad,
      link,
    });
  }
}

// 3️⃣ Salida
if (jobs.length === 0) {
  return [{ json: { mensaje: "No se extrajeron ofertas válidas", total: 0 , link:"w."} }];
}

return jobs.map((x) => ({ json: x }));

// Soporta JSON (hits.hits) o respuesta String (intenta parsear; fallback HTML)
let text = $json.data ?? $json.body ?? null;
let hits = [];

if (typeof text === "string") {
  let t = text.trim();
  if (t.startsWith(")]}'")) t = t.slice(4).trim(); // anti-XSSI
  try { const js = JSON.parse(t); hits = js?.hits?.hits || []; } catch {}
} else if ($json?.hits?.hits) {
  hits = $json.hits.hits;
}

if (!hits.length) {
  // Fallback: intentar raspar enlaces si fue HTML
  const html = typeof text === "string" ? text : "";
  const re = /<a[^>]+href="([^"]+)"[^>]*>(.*?)<\/a>/gi;
  const alt = [];
  let m; 
  while ((m = re.exec(html)) && alt.length < 25) {
    const link = m[1];
    const titulo = (m[2] || "").replace(/<[^>]+>/g, "").trim();
    if (titulo && link) alt.push({ fuente:"Santander", titulo, link, region:"Sin región", modalidad:"Presencial" });
  }
  return alt.length ? alt.map(x => ({ json: x })) 
                    : [{ json: { fuente: "Santander", mensaje: "Sin resultados o respuesta no JSON" } }];
}

const out = hits.map(h => {
  const s = h._source || h;
  const titulo = s?.title || "Sin título";
  const link = s?.applyUrl || s?.url || "";
  const region = (s?.location && (s.location.display_name || s.location.city)) || s?.city || "Sin región";
  const modalidad = /remot/i.test(((s?.modality || "") + " " + region)) ? "Remoto" : "Presencial";
  return { fuente: "Santander", titulo, link, region, modalidad };
});

return out.map(x => ({ json: x }));

// === PARSER LABORUM v8 - Limpieza avanzada ===
const raw = $json.data || $json.body || "";
const text = String(raw).replace(/\r/g, "").trim();

// --- Expresión más flexible ---
const regex = /\[(?:Nuevo|Actualizado|\#\#\#)?[^\]]*?(Práctica[^\#\n]+|Alumno en Práctica[^\#\n]*)[^\]]*?\#\#\#\s*([A-ZÁÉÍÓÚÑÜ][^\#\n]+?)\s*(?:\#\#\#\s*([^#\n]+?))?\s*\#\#\#\s*(Presencial|Remoto|Híbrido)[^\]]*\]\((https:\/\/www\.laborum\.cl\/empleos\/[^\)]+)\)/gi;

const out = [];
let match;

while ((match = regex.exec(text))) {
  let titulo   = (match[1] || "").replace(/[-–—]+/g, "").trim();
  let empresa  = (match[2] || "").trim();
  let region   = (match[3] || "").trim();
  let modalidad= (match[4] || "").trim() || "Presencial";
  let link     = (match[5] || "").trim();

  // --- LIMPIEZA ---
  // 1. Eliminar Markdown residual e imágenes
  empresa = empresa.replace(/!\[.*?\]\(.*?\)/g, "").replace(/\s{2,}/g, " ").trim();
  region  = region.replace(/!\[.*?\]\(.*?\)/g, "").replace(/\s{2,}/g, " ").trim();
  titulo  = titulo.replace(/!\[.*?\]\(.*?\)/g, "").replace(/\s{2,}/g, " ").trim();

  // 2. Si empresa contiene "Región", intercambiar
  if (/Región/i.test(empresa)) {
    region = empresa;
    empresa = "Sin empresa";
  }

  // 3. Si región está vacía o sin "Región", intentar buscar dentro del bloque
  if (!/Región/i.test(region)) {
    const m = empresa.match(/([A-ZÁÉÍÓÚÑÜa-zñü\s]+,\s*Región\s*[A-ZÁÉÍÓÚÑÜIVX]+)/);
    if (m) {
      region = m[1].trim();
      empresa = empresa.replace(m[0], "").trim();
    }
  }

  // 4. Recortar empresa si es descripción larga
  if (empresa.split(/\s+/).length > 6) {
    empresa = empresa
      .replace(/Somos.*|Compañía.*|Empresa.*|holding.*|S\.A\..*|Sociedad.*|Limitada.*|con.*|del.*|la.*|los.*|las.*|de.*$/gi, "")
      .trim();
  }

  // 5. Limpieza final
  empresa = empresa.replace(/[,.-]+$/g, "").trim();
  region  = region.replace(/^Región\s*/i, "").trim() || "Sin región";
  titulo  = titulo.replace(/-{2,}|={2,}/g, "").trim();

  if (link) {
    out.push({
      fuente: "Laborum",
      titulo,
      empresa: empresa || "Sin empresa",
      region,
      modalidad,
      link
    });
  }
}

// --- Fallback mínimo ---
if (out.length === 0) {
  const altRegex = /\[\s*(Práctica[^\#\n]+|Alumno en Práctica[^\#\n]*)[^\]]*\]\((https:\/\/www\.laborum\.cl\/empleos\/[^\)]+)\)/gi;
  let m;
  while ((m = altRegex.exec(text))) {
    out.push({
      fuente: "Laborum",
      titulo: (m[1] || "").trim(),
      empresa: "Sin empresa",
      region: "Sin región",
      modalidad: "Presencial",
      link: m[2]
    });
  }
}

// --- Eliminar duplicados ---
const unicos = out.filter((v, i, a) => a.findIndex(t => t.link === v.link) === i);

return unicos.length
  ? unicos.map(x => ({ json: x }))
  : [{ json: { fuente: "Laborum", mensaje: "Sin resultados válidos", total: 0 } }];

// ================================================
// FILTRAR POR PREFERENCIAS (Versión FINAL 2025 AJUSTADA)
// Mantiene tus llamadas EXACTAS a Split In Batches1
// Incluye mapa de equivalencias de regiones
// ================================================


// === Helpers ===
function normalize(s) {
    return String(s || "")
        .normalize("NFD")
        .replace(/[\u0300-\u036f]/g, "")
        .toLowerCase()
        .trim();
}

// Canon modalidad → remoto / hibrido / presencial
function canonModa(s) {
    const t = normalize(s);
    if (!t) return "";

    if (t.includes("remot")) return "remoto";
    if (t.includes("hibrid") || t.includes("mixt") || t.includes("semi"))
        return "hibrido";
    if (t.includes("presenc") || t.includes("oficin") || t.includes("onsite"))
        return "presencial";

    return "";
}


// ================================================
// NUEVO: MAPA DE REGIONES → Para igualar VIII, Concepción, Bío Bío, etc.
// ================================================
const REGION_MAP = {
    "region metropolitana": ["rm", "metropolitana", "santiago"],
    "region de valparaiso": ["region v", "valparaiso"],
    "region del biobio": ["region viii", "concepcion", "bio bio", "bío bío"],
    "region de coquimbo": ["region iv", "coquimbo", "la serena"],
    "region de atacama": ["region iii"],
    "region de antofagasta": ["region ii", "antofagasta"],
    "region de tarapaca": ["region i", "iquique"],
    "region de arica y parinacota": ["region xv", "arica"],
    "region del maule": ["region vii", "maule", "talca"],
    "region de ohiggins": ["region vi", "ohiggins", "rancagua"],
    "region de los lagos": ["region x", "puerto montt"],
    "region de los rios": ["region xiv", "valdivia"],
    "region de aysen": ["region xi", "aysen"],
    "region de magallanes": ["region xii", "punta arenas"],
};

// Canon región
function canonRegion(txt) {
    const clean = normalize(txt);

    for (const [std, aliases] of Object.entries(REGION_MAP)) {
        if (clean.includes(std)) return std;

        for (const a of aliases) {
            if (clean.includes(a)) return std;
        }
    }

    return clean; // fallback
}


// ================================================
// 1) OBTENER PREFERENCIAS (mantengo EXACTAMENTE tus llamadas)
// ================================================
const prefRegionRaw = $('Split In Batches1').first().json.region || "";
const prefModaRaw   = $('Split In Batches1').first().json.modalidad || "";

const prefRegion = canonRegion(prefRegionRaw);
const prefModa   = canonModa(prefModaRaw);


// ================================================
// 2) FILTROS AJUSTADOS
// ================================================
function matchRegion(prefRegion, regionItem) {
    if (!prefRegion) return true;
    return canonRegion(regionItem) === prefRegion;
}

function matchModa(prefModa, itemModa) {
    if (!prefModa) return true;

    if (prefModa === "remoto") {
        return itemModa === "remoto" || itemModa === "hibrido";
    }

    return itemModa === prefModa;
}


// ================================================
// 3) PROCESAR OFERTAS (mantengo EXACTO tu bloque)
// ================================================
const out = [];

for (const { json } of items) {
    if (!json.link) continue;

    const regionItem = json.region || "";

    const modaItem =
        canonModa(json.modalidad) ||
        canonModa(json.region) ||
        canonModa(json.titulo) ||
        "presencial";

    if (matchRegion(prefRegion, regionItem) && matchModa(prefModa, modaItem)) {
        out.push({ json: { ...json, modalidad: modaItem } });
    }
}


// ================================================
// 4) DEBUG SI NO HAY COINCIDENCIAS
// ================================================
if (out.length === 0) {
    return [{
        json: {
            msg: "Sin coincidencias",
            prefRegionRaw,
            prefRegion,
            prefModa,
            total_in: items.length
        }
    }];
}


// ================================================
// 5) RESULTADO FINAL
// ================================================
return out;

// Limpia parámetros de tracking en los links antes de deduplicar
const stripParams = [
  'utm_source','utm_medium','utm_campaign','utm_term','utm_content',
  'gclid','fbclid','fccid','vjs','from','ad','dq'
];

return items.map(({ json }) => {
  if (json.link && typeof json.link === 'string') {
    // normaliza entidades HTML tipo &amp;
    let href = json.link.trim().replace(/&amp;/g, '&');

    try {
      // Soporta links relativos (Indeed a veces trae /rc/clk?...):
      if (href.startsWith('/')) href = 'https://cl.indeed.com' + href;

      const u = new URL(href);
      // elimina parámetros de tracking
      stripParams.forEach(p => u.searchParams.delete(p));
      // quita el fragmento #...
      u.hash = '';
      // normaliza protocolo a https si venía http
      if (u.protocol === 'http:') u.protocol = 'https:';

      json.link = u.toString();
    } catch {
      // si falla el parseo, al menos quita &amp; y espacios
      json.link = href;
    }
  }
  return { json };
});

// === APLANADOR UNIVERSAL + EMAIL BUILDER PRO (Versión Fer 2025) ===

// -----------------------------------------
// Helpers generales
// -----------------------------------------
function unbox(obj) {
  let j = obj?.json ?? obj ?? {};
  if (j && typeof j === "object" && j.json && typeof j.json === "object") j = j.json;
  for (const k of ["oferta", "payload", "data", "result", "resultado", "item"])
    if (j && typeof j[k] === "object" && j[k] !== null) {
      j = j[k];
      break;
    }
  return j || {};
}

function esc(s) {
  return String(s ?? "").replace(/[&<>\"']/g, m => ({ 
    "&": "&amp;", 
    "<": "&lt;", 
    ">": "&gt;", 
    "\"": "&quot;", 
    "'": "&#39;" 
  }[m]));
}

function pick(...xs) {
  for (const x of xs) {
    if (x !== undefined && x !== null && String(x).trim() !== "") return x;
  }
  return "";
}

// -----------------------------------------
// 1) Tomar preferencias REALES del usuario actual
//    (desde Split In Batches → usuario activo)
// -----------------------------------------


const prefRegion = $('Split In Batches1').first().json.region;
const prefModalidad = $('Split In Batches1').first().json.modalidad;
const emailTo = $('Split In Batches1').first().json.email;


// -----------------------------------------
// 2) Aplanar resultados
// -----------------------------------------
const filasRaw = items.map(unbox);

const filas = filasRaw
  .map(r => ({
    fuente: pick(r.fuente, r.source, r.origen, "desconocida"),
    titulo: pick(r.titulo, r.title, r.nombre),
    empresa: pick(r.empresa, r.company, r.organizacion, "Sin empresa"),
    region: pick(r.region, r.ubicacion, r.location, "Sin región"),
    modalidad: pick(r.modalidad, r.mode, r.tipoModalidad, "Presencial"),
    link: pick(r.link, r.url, r.href)
  }))
  .filter(r => r.titulo || r.link);

// total resultados
const total = filas.length;

// -----------------------------------------
// 3) Métricas por fuente
// -----------------------------------------
const porFuente = {};
for (const r of filas) porFuente[r.fuente] = (porFuente[r.fuente] || 0) + 1;

const stats = Object.entries(porFuente)
  .map(([k, v]) => `<li><b>${esc(k)}</b>: ${v}</li>`)
  .join("");

// -----------------------------------------
// 4) Tabla HTML
// -----------------------------------------
const rowsHtml = filas
  .map(
    (r, i) => `
<tr>
  <td style="text-align:center;color:#555;">${i + 1}</td>
  <td style="font-weight:500;">${esc(r.titulo)}</td>
  <td style="color:#444;">${esc(r.empresa)}</td>
  <td style="color:#777;">${esc(r.fuente)}</td>
  <td style="text-align:center;">
    ${r.link ? `<a href="${esc(r.link)}" target="_blank" style="color:#2563eb;text-decoration:none;">Ver oferta</a>` : ""}
  </td>
</tr>`
  )
  .join("");

// -----------------------------------------
// 5) Construcción del HTML final
// -----------------------------------------
const html = `
<div style="font-family:'Segoe UI',Arial,sans-serif;max-width:900px;margin:auto;background:#f9fafb;border-radius:8px;overflow:hidden;">
  <div style="background:#2563eb;color:white;padding:16px 20px;">
    <h2 style="margin:0;font-weight:600;">Resultados de búsqueda de prácticas</h2>
    <p style="margin:4px 0 0;font-size:14px;">Total encontrados: <b>${total}</b></p>
  </div>

  <div style="padding:20px 24px;">

    <div style="margin-bottom:10px;padding:12px 16px;background:#eef2ff;border-radius:6px;">
      <p style="margin:0;font-size:14px;color:#374151;">
        <b>Región preferida:</b> ${esc(prefRegion)}<br>
        <b>Modalidad deseada:</b> ${esc(prefModalidad)}
      </p>
    </div>

    ${
      stats
        ? `<p style="margin-bottom:4px;">Distribución por fuente:</p><ul style="margin-top:4px;">${stats}</ul>`
        : "<p><i>Sin conteo por fuente.</i></p>"
    }

    <table border="0" cellpadding="8" cellspacing="0" style="border-collapse:collapse;width:100%;margin-top:10px;background:white;border:1px solid #e5e7eb;">
      <thead style="background:#f3f4f6;">
        <tr>
          <th style="text-align:center;">#</th>
          <th style="text-align:left;">Título</th>
          <th style="text-align:left;">Empresa</th>
          <th style="text-align:left;">Fuente</th>
          <th style="text-align:center;">Link</th>
        </tr>
      </thead>
      <tbody>
        ${
          rowsHtml ||
          `<tr><td colspan="5" style="text-align:center;padding:16px;color:#888;">Sin resultados válidos</td></tr>`
        }
      </tbody>
    </table>

    <p style="margin-top:16px;font-size:12px;color:#6b7280;text-align:center;">
      Correo generado automáticamente por <b>n8n</b>. No responder a este mensaje.
    </p>
  </div>
</div>
`;

const subject = total > 0
  ? `Resultados: ${total} prácticas encontradas`
  : `No se encontraron prácticas`;

return [
  {
    json: {
      to: emailTo,
      subject,
      html,
      text: `Se encontraron ${total} resultados.`,
      total
    }
  }
];

// Toma el usuario actual desde "Split In Batches"
const u = $items('Split In Batches1', 0, 0)?.json || {};

// helpers del punto 2
function norm(s){
  return String(s || '')
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '')
    .toLowerCase().trim();
}
function formatLocal(dt){
  const pad = n => String(n).padStart(2,'0');
  return `${dt.getFullYear()}-${pad(dt.getMonth()+1)}-${pad(dt.getDate())} ${pad(dt.getHours())}:${pad(dt.getMinutes())}:${pad(dt.getSeconds())}`;
}
function nextFrom(freq, base = new Date()){
  const f = norm(freq);
  const d = new Date(base);
  if (/30\s*min/.test(f))              d.setMinutes(d.getMinutes() + 30);
  else if (/^4\s*dias?$/.test(f))      d.setDate(d.getDate() + 4);
  else if (/^15\s*dias?$/.test(f))     d.setDate(d.getDate() + 15);
  else if (/semana/.test(f))           d.setDate(d.getDate() + 7);
  else if (/mes/.test(f))              d.setMonth(d.getMonth() + 1);
  else if (/24\s*h/.test(f) || /dia/.test(f))
                                       d.setDate(d.getDate() + 1);
  else                                 d.setDate(d.getDate() + 7);
  return formatLocal(d);
}

return [{
  json: {
    Email: u.email || u.Email,                // clave para buscar la fila
    next_run: nextFrom(u.frecuencia || u.Frecuencia)
  }
}];

const limiteRow = $items('HU13 - Get Límite (HU6)')[0].json;
const consumoRow = $items('HU13 - Get Consumo Actual')[0].json;

const limite = Number(limiteRow.limite || 0);
const usados = Number(consumoRow.usados || 0);

return [{
  exceeded: usados >= limite,
  limite,
  usados
}];

// Obtener datos desde los nodos anteriores
const limiteRow = $items('HU13 - Get Límite (HU6)')[0].json;
const consumoRow = $items('HU13 - Get Consumo Actual')[0].json;
const emailRow = $items('HU13 - Normalizar Email')[0].json;

// Convertir valores
const limite = Number(limiteRow.limite || 0);
const usados_actual = Number(consumoRow.usados || 0);

// Incremento
const usados_nuevo = usados_actual + 1;

// Mes actual YYYY-MM
const mes = new Date().toISOString().slice(0, 7);

// Timestamp
const actualizado_ts = new Date().toISOString();

// Devolver un único item limpio
return [
  {
    email: emailRow.email,
