// Soporta JSON (hits.hits) o respuesta String (intenta parsear; fallback HTML)
let text = $json.data ?? $json.body ?? null;
let hits = [];

if (typeof text === "string") {
  let t = text.trim();
  if (t.startsWith(")]}'")) t = t.slice(4).trim(); // anti-XSSI
  try { const js = JSON.parse(t); hits = js?.hits?.hits || []; } catch {}
} else if ($json?.hits?.hits) {
  hits = $json.hits.hits;
}

if (!hits.length) {
  // Fallback: intentar raspar enlaces si fue HTML
  const html = typeof text === "string" ? text : "";
  const re = /<a[^>]+href="([^"]+)"[^>]*>(.*?)<\/a>/gi;
  const alt = [];
  let m; 
  while ((m = re.exec(html)) && alt.length < 25) {
    const link = m[1];
    const titulo = (m[2] || "").replace(/<[^>]+>/g, "").trim();
    if (titulo && link) alt.push({ fuente:"Santander", titulo, link, region:"Sin región", modalidad:"Presencial" });
  }
  return alt.length ? alt.map(x => ({ json: x })) 
                    : [{ json: { fuente: "Santander", mensaje: "Sin resultados o respuesta no JSON" } }];
}

const out = hits.map(h => {
  const s = h._source || h;
  const titulo = s?.title || "Sin título";
  const link = s?.applyUrl || s?.url || "";
  const region = (s?.location && (s.location.display_name || s.location.city)) || s?.city || "Sin región";
  const modalidad = /remot/i.test(((s?.modality || "") + " " + region)) ? "Remoto" : "Presencial";
  return { fuente: "Santander", titulo, link, region, modalidad };
});

return out.map(x => ({ json: x }));

// === PARSER LABORUM v8 - Limpieza avanzada ===
const raw = $json.data || $json.body || "";
const text = String(raw).replace(/\r/g, "").trim();

// --- Expresión más flexible ---
const regex = /\[(?:Nuevo|Actualizado|\#\#\#)?[^\]]*?(Práctica[^\#\n]+|Alumno en Práctica[^\#\n]*)[^\]]*?\#\#\#\s*([A-ZÁÉÍÓÚÑÜ][^\#\n]+?)\s*(?:\#\#\#\s*([^#\n]+?))?\s*\#\#\#\s*(Presencial|Remoto|Híbrido)[^\]]*\]\((https:\/\/www\.laborum\.cl\/empleos\/[^\)]+)\)/gi;

const out = [];
let match;

while ((match = regex.exec(text))) {
  let titulo   = (match[1] || "").replace(/[-–—]+/g, "").trim();
  let empresa  = (match[2] || "").trim();
  let region   = (match[3] || "").trim();
  let modalidad= (match[4] || "").trim() || "Presencial";
  let link     = (match[5] || "").trim();

  // --- LIMPIEZA ---
  // 1. Eliminar Markdown residual e imágenes
  empresa = empresa.replace(/!\[.*?\]\(.*?\)/g, "").replace(/\s{2,}/g, " ").trim();
  region  = region.replace(/!\[.*?\]\(.*?\)/g, "").replace(/\s{2,}/g, " ").trim();
  titulo  = titulo.replace(/!\[.*?\]\(.*?\)/g, "").replace(/\s{2,}/g, " ").trim();

  // 2. Si empresa contiene "Región", intercambiar
  if (/Región/i.test(empresa)) {
    region = empresa;
    empresa = "Sin empresa";
  }

  // 3. Si región está vacía o sin "Región", intentar buscar dentro del bloque
  if (!/Región/i.test(region)) {
    const m = empresa.match(/([A-ZÁÉÍÓÚÑÜa-zñü\s]+,\s*Región\s*[A-ZÁÉÍÓÚÑÜIVX]+)/);
    if (m) {
      region = m[1].trim();
      empresa = empresa.replace(m[0], "").trim();
    }
  }

  // 4. Recortar empresa si es descripción larga
  if (empresa.split(/\s+/).length > 6) {
    empresa = empresa
      .replace(/Somos.*|Compañía.*|Empresa.*|holding.*|S\.A\..*|Sociedad.*|Limitada.*|con.*|del.*|la.*|los.*|las.*|de.*$/gi, "")
      .trim();
  }

  // 5. Limpieza final
  empresa = empresa.replace(/[,.-]+$/g, "").trim();
  region  = region.replace(/^Región\s*/i, "").trim() || "Sin región";
  titulo  = titulo.replace(/-{2,}|={2,}/g, "").trim();

  if (link) {
    out.push({
      fuente: "Laborum",
      titulo,
      empresa: empresa || "Sin empresa",
      region,
      modalidad,
      link
    });
  }
}

// --- Fallback mínimo ---
if (out.length === 0) {
  const altRegex = /\[\s*(Práctica[^\#\n]+|Alumno en Práctica[^\#\n]*)[^\]]*\]\((https:\/\/www\.laborum\.cl\/empleos\/[^\)]+)\)/gi;
  let m;
  while ((m = altRegex.exec(text))) {
    out.push({
      fuente: "Laborum",
      titulo: (m[1] || "").trim(),
      empresa: "Sin empresa",
      region: "Sin región",
      modalidad: "Presencial",
      link: m[2]
    });
  }
}

// --- Eliminar duplicados ---
const unicos = out.filter((v, i, a) => a.findIndex(t => t.link === v.link) === i);

return unicos.length
  ? unicos.map(x => ({ json: x }))
  : [{ json: { fuente: "Laborum", mensaje: "Sin resultados válidos", total: 0 } }];

const limiteRow = $items('HU13 - Get Límite (HU4)')[0].json;
const consumoRow = $items('HU13 - Get Consumo Actual(HU4)')[0].json;

