// FILTRAR POR PREFERENCIAS (Versión FINAL 2025 AJUSTADA)
// Mantiene tus llamadas EXACTAS a Split In Batches1
// Incluye mapa de equivalencias de regiones
// ================================================


// === Helpers ===
function normalize(s) {
    return String(s || "")
        .normalize("NFD")
        .replace(/[\u0300-\u036f]/g, "")
        .toLowerCase()
        .trim();
}

// Canon modalidad → remoto / hibrido / presencial
function canonModa(s) {
    const t = normalize(s);
    if (!t) return "";

    if (t.includes("remot")) return "remoto";
    if (t.includes("hibrid") || t.includes("mixt") || t.includes("semi"))
        return "hibrido";
    if (t.includes("presenc") || t.includes("oficin") || t.includes("onsite"))
        return "presencial";

    return "";
}


// ================================================
// NUEVO: MAPA DE REGIONES → Para igualar VIII, Concepción, Bío Bío, etc.
// ================================================
const REGION_MAP = {
    "region metropolitana": ["rm", "metropolitana", "santiago"],
    "region de valparaiso": ["region v", "valparaiso"],
    "region del biobio": ["region viii", "concepcion", "bio bio", "bío bío"],
    "region de coquimbo": ["region iv", "coquimbo", "la serena"],
    "region de atacama": ["region iii"],
    "region de antofagasta": ["region ii", "antofagasta"],
    "region de tarapaca": ["region i", "iquique"],
    "region de arica y parinacota": ["region xv", "arica"],
    "region del maule": ["region vii", "maule", "talca"],
    "region de ohiggins": ["region vi", "ohiggins", "rancagua"],
    "region de los lagos": ["region x", "puerto montt"],
    "region de los rios": ["region xiv", "valdivia"],
    "region de aysen": ["region xi", "aysen"],
    "region de magallanes": ["region xii", "punta arenas"],
};

// Canon región
function canonRegion(txt) {
    const clean = normalize(txt);

    for (const [std, aliases] of Object.entries(REGION_MAP)) {
        if (clean.includes(std)) return std;

        for (const a of aliases) {
            if (clean.includes(a)) return std;
        }
    }

    return clean; // fallback
}


// ================================================
// 1) OBTENER PREFERENCIAS (mantengo EXACTAMENTE tus llamadas)
// ================================================
const prefRegionRaw =$('Obtener Preferencias(HU4)').first().json['Región'] || "";
const prefModaRaw   = $('Obtener Preferencias(HU4)').first().json.Modalidad|| "";

const prefRegion = canonRegion(prefRegionRaw);
const prefModa   = canonModa(prefModaRaw);


// ================================================
// 2) FILTROS AJUSTADOS
// ================================================
function matchRegion(prefRegion, regionItem) {
    if (!prefRegion) return true;
    return canonRegion(regionItem) === prefRegion;
}

function matchModa(prefModa, itemModa) {
    if (!prefModa) return true;

    if (prefModa === "remoto") {
        return itemModa === "remoto" || itemModa === "hibrido";
    }

    return itemModa === prefModa;
}


// ================================================
// 3) PROCESAR OFERTAS (mantengo EXACTO tu bloque)
// ================================================
const out = [];

for (const { json } of items) {
    if (!json.link) continue;

    const regionItem = json.region || "";

    const modaItem =
        canonModa(json.modalidad) ||
        canonModa(json.region) ||
        canonModa(json.titulo) ||
        "presencial";

    if (matchRegion(prefRegion, regionItem) && matchModa(prefModa, modaItem)) {
        out.push({ json: { ...json, modalidad: modaItem } });
    }
}


// ================================================
// 4) DEBUG SI NO HAY COINCIDENCIAS
// ================================================
if (out.length === 0) {
    return [{
        json: {
            msg: "Sin coincidencias",
            prefRegionRaw,
            prefRegion,
            prefModa,
            total_in: items.length
        }
    }];
}


// ================================================
// 5) RESULTADO FINAL
// ================================================
return out;

// Limpia parámetros de tracking en los links antes de deduplicar
const stripParams = [
  'utm_source','utm_medium','utm_campaign','utm_term','utm_content',
  'gclid','fbclid','fccid','vjs','from','ad','dq'
];

return items.map(({ json }) => {
  if (json.link && typeof json.link === 'string') {
    // normaliza entidades HTML tipo &amp;
    let href = json.link.trim().replace(/&amp;/g, '&');

    try {
      // Soporta links relativos (Indeed a veces trae /rc/clk?...):
      if (href.startsWith('/')) href = 'https://cl.indeed.com' + href;

      const u = new URL(href);
      // elimina parámetros de tracking
      stripParams.forEach(p => u.searchParams.delete(p));
      // quita el fragmento #...
      u.hash = '';
      // normaliza protocolo a https si venía http
      if (u.protocol === 'http:') u.protocol = 'https:';

      json.link = u.toString();
    } catch {
      // si falla el parseo, al menos quita &amp; y espacios
      json.link = href;
    }
  }
  return { json };
});

// === APLANADOR UNIVERSAL + EMAIL BUILDER PRO (Versión Fer 2025) ===

// -----------------------------------------
// Helpers generales
// -----------------------------------------
function unbox(obj) {
  let j = obj?.json ?? obj ?? {};
  if (j && typeof j === "object" && j.json && typeof j.json === "object") j = j.json;
  for (const k of ["oferta", "payload", "data", "result", "resultado", "item"])
    if (j && typeof j[k] === "object" && j[k] !== null) {
      j = j[k];
      break;
    }
  return j || {};
}

function esc(s) {
  return String(s ?? "").replace(/[&<>\"']/g, m => ({ 
    "&": "&amp;", 
    "<": "&lt;", 
    ">": "&gt;", 
    "\"": "&quot;", 
    "'": "&#39;" 
  }[m]));
}

function pick(...xs) {
  for (const x of xs) {
    if (x !== undefined && x !== null && String(x).trim() !== "") return x;
  }
  return "";
}

// -----------------------------------------
// 1) Tomar preferencias REALES del usuario actual
//    (desde Split In Batches → usuario activo)
// -----------------------------------------


const prefRegion =$('Obtener Preferencias(HU4)').first().json['Región']; 
const prefModalidad =$('Obtener Preferencias(HU4)').first().json.Modalidad ;
const emailTo =$('HU4 - Form Trigger').first().json.Email;


// -----------------------------------------
// 2) Aplanar resultados
// -----------------------------------------
const filasRaw = items.map(unbox);

const filas = filasRaw
  .map(r => ({
    fuente: pick(r.fuente, r.source, r.origen, "desconocida"),
    titulo: pick(r.titulo, r.title, r.nombre),
    empresa: pick(r.empresa, r.company, r.organizacion, "Sin empresa"),
    region: pick(r.region, r.ubicacion, r.location, "Sin región"),
    modalidad: pick(r.modalidad, r.mode, r.tipoModalidad, "Presencial"),
    link: pick(r.link, r.url, r.href)
  }))
  .filter(r => r.titulo || r.link);

// total resultados
const total = filas.length;

// -----------------------------------------
// 3) Métricas por fuente
// -----------------------------------------
const porFuente = {};
for (const r of filas) porFuente[r.fuente] = (porFuente[r.fuente] || 0) + 1;

const stats = Object.entries(porFuente)
  .map(([k, v]) => `<li><b>${esc(k)}</b>: ${v}</li>`)
  .join("");

// -----------------------------------------
// 4) Tabla HTML
// -----------------------------------------
const rowsHtml = filas
  .map(
    (r, i) => `
<tr>
  <td style="text-align:center;color:#555;">${i + 1}</td>
  <td style="font-weight:500;">${esc(r.titulo)}</td>
  <td style="color:#444;">${esc(r.empresa)}</td>
  <td style="color:#777;">${esc(r.fuente)}</td>
  <td style="text-align:center;">
    ${r.link ? `<a href="${esc(r.link)}" target="_blank" style="color:#2563eb;text-decoration:none;">Ver oferta</a>` : ""}
  </td>
</tr>`
  )
  .join("");

// -----------------------------------------
// 5) Construcción del HTML final
// -----------------------------------------
const html = `
<div style="font-family:'Segoe UI',Arial,sans-serif;max-width:900px;margin:auto;background:#f9fafb;border-radius:8px;overflow:hidden;">
  <div style="background:#2563eb;color:white;padding:16px 20px;">
    <h2 style="margin:0;font-weight:600;">Reporte Inmediato</h2>
    <p style="margin:4px 0 0;font-size:14px;">Total encontrados: <b>${total}</b></p>
  </div>

  <div style="padding:20px 24px;">

    <div style="margin-bottom:10px;padding:12px 16px;background:#eef2ff;border-radius:6px;">
      <p style="margin:0;font-size:14px;color:#374151;">
        <b>Región preferida:</b> ${esc(prefRegion)}<br>
        <b>Modalidad deseada:</b> ${esc(prefModalidad)}
      </p>
    </div>

    ${
      stats
        ? `<p style="margin-bottom:4px;">Distribución por fuente:</p><ul style="margin-top:4px;">${stats}</ul>`
        : "<p><i>Sin conteo por fuente.</i></p>"
    }

    <table border="0" cellpadding="8" cellspacing="0" style="border-collapse:collapse;width:100%;margin-top:10px;background:white;border:1px solid #e5e7eb;">
      <thead style="background:#f3f4f6;">
        <tr>
          <th style="text-align:center;">#</th>
          <th style="text-align:left;">Título</th>
          <th style="text-align:left;">Empresa</th>
          <th style="text-align:left;">Fuente</th>
          <th style="text-align:center;">Link</th>
        </tr>
      </thead>
      <tbody>
        ${
          rowsHtml ||
          `<tr><td colspan="5" style="text-align:center;padding:16px;color:#888;">Sin resultados válidos</td></tr>`
        }
      </tbody>
    </table>

    <p style="margin-top:16px;font-size:12px;color:#6b7280;text-align:center;">
      Correo generado automáticamente por <b>n8n</b>. No responder a este mensaje.
    </p>
  </div>
</div>
`;

const subject = total > 0
  ? `Resultados: ${total} prácticas encontradas`
  : `No se encontraron prácticas`;

return [
  {
    json: {
      to: emailTo,
      subject,
      html,
      text: `Se encontraron ${total} resultados.`,
      total
    }
  }
];
